/*
 * This file was generated by the Gradle 'init' task.
 */

plugins {
    `java-library`
    `maven-publish`
}

repositories {
    mavenLocal()
    maven {
        url = uri("https://maven.pkg.github.com/APDevTeam/Movecraft")
    }

    maven {
        url = uri("https://repo.maven.apache.org/maven2/")
    }

    maven {
        url = uri("https://hub.spigotmc.org/nexus/content/repositories/snapshots/")
    }

    maven {
        url = uri("https://maven.enginehub.org/repo/")
    }

    maven {
        url = uri("https://jitpack.io")
    }
}

dependencies {
    api(libs.com.google.code.gson.gson)
    api(libs.org.jetbrains.annotations.java5)
    api(libs.com.sk89q.worldedit.worldedit.core)
    api(libs.com.sk89q.worldedit.worldedit.bukkit)
    compileOnly(libs.org.spigotmc.spigot.api)
    compileOnly(libs.net.countercraft.movecraft)
    compileOnly(libs.com.github.milkbowl.vaultapi)
    compileOnly(libs.it.unimi.dsi.fastutil)
    compileOnly(libs.org.roaringbitmap.roaringbitmap)
}

group = "net.countercraft.movecraft.repair"
version = "1.0.0_beta-4"
description = "Movecraft-Repair"
java.sourceCompatibility = JavaVersion.VERSION_13

publishing {
    publications.create<MavenPublication>("maven") {
        from(components["java"])
    }
}

tasks.withType<JavaCompile>() {
    options.encoding = "UTF-8"
}

tasks.withType<Javadoc>() {
    options.encoding = "UTF-8"
}
